mapper.select:
select count(*) count from t_equip_base b left join item item1 on item1.id = b.status_id left join user u on u.id = b.applicant left join t_sys_unit unit on unit.id=b.unit_id left join item item2 on item2.id=b.reviewstatus_id left join item item3 on item3.id = unit.gradetype
${key} #{item}
and delstatus > -1

SELECT b.id, b.internalorg, b.compile, b.applytime, b.applicant, b.applyname, b.unit_id, b.applycode, b.lastUpdateTime, b.status_id, b.reviewstatus_id, b.reviewcotent, b.reviewscore, b.process_id, b.log_id, b.classify_id, b.creator_id, b.area_id, b.createtime, b.updator_id, b.starttime, b.endtime, b.process_id, unit.name as unitName, item3.id grade, item3.itemvalue gradeName, item1.itemvalue as statusName,u.name as applicantName,unit.name as unitName,item2.itemvalue as reviewstatusName FROM t_equip_base b left join item item1 on item1.id = b.status_id left join user u on u.id = b.applicant left join t_sys_unit unit on unit.id=b.unit_id left join item item2 on item2.id=b.reviewstatus_id left join item item3 on item3.id = unit.gradetype
${key} #{item}
and delstatus > -1
ORDER BY ${sort} ${order} LIMIT #{from}, #{size}

SELECT count(*)
from t_approve_log log inner join t_equip_base p on log.business_id = p.id inner join t_approve_process pro on log.process_id = pro.id inner join t_approve_step step on log.step_id = step.id left join t_sys_unit unit1 on unit1.id = log.stepUnit_id left join t_sys_unit unit2 on unit2.id = p.unit_id left join user u1 on u1.id = log.stepUser_id left join user u2 on u2.id = p.applicant left join user u3 on u3.id = log.approvetor_id left join user_role ur1 on ur1.user_id = u1.id left join user_role ur2 on ur2.user_id = u3.id left join sysarea area on area.id = unit2.sysarea_id left join item item1 on item1.id = p.status_id left join item item2 on item2.id = log.status_id left join item item3 on item3.id = log.reviewstatus_id left join item item4 on item4.id = p.reviewStatus_id left join item item5 on item5.id = p.classify_id where log.datasign = '1' and log.status_id <> '468496a0-7692-4b3b-838f-612bd884f9d4' and (unit1.id = #{user.unitId} OR u1.id = #{user.id} or u3.id = #{user.id} or log.steprole_id = #{user.roleId} )
and ${q}
and
${key} #{item}

