from t_approve_log log inner join t_equip_base p on log.business_id = p.id inner join t_approve_process pro on log.process_id = pro.id inner join t_approve_step step on log.step_id = step.id left join t_sys_unit unit1 on unit1.id = log.stepUnit_id left join t_sys_unit unit2 on unit2.id = p.unit_id left join user u1 on u1.id = log.stepUser_id left join user u2 on u2.id = p.applicant left join user u3 on u3.id = log.approvetor_id left join sysarea area on area.id = p.area_id left join item item1 on item1.id = p.status_id left join item item2 on item2.id = log.status_id left join item item3 on item3.id = log.reviewstatus_id left join item item4 on item4.id = p.reviewStatus_id where log.datasign = '1' and log.status_id <> '468496a0-7692-4b3b-838f-612bd884f9d4' and (unit1.id = #{user.unitId} OR u1.id = #{user.id} or u3.id = #{user.id} or log.steprole_id = #{user.roleId} )
and ${q}
