${key} #{item}
ORDER BY ${sort} ${order} LIMIT #{from}, #{size}

SELECT p.id as projectId, p.applycode projectNumber, p.applyname projectName, p.reviewStatus_id projectReviewStatus, p.reviewCotent projectReviewContent, p.reviewScore projectReviewScore, p.log_id projectLastLogId, p.applytime projectDeclareTime, pro.id processId, pro.number processNumber, pro.name processName, step.id stepId, step.stepnumber stepNumber, step.parentstepnumber stepParentNumber, step.stepname stepName, log.id logId, log.createtime logCreatetime, log.lastupdatetime logLastupdatetime, log.stepNumber logStepNumber, log.logNumber longNumber, log.starttime logStarttime, log.endtime logEndtime, unit1.id approveUnitId, unit1.name approveUnitName, unit2.id declareUnitId, unit2.name declareUnitName, u3.id actualApproverId, u3.name actualApproverName, unit3.id actualApproveUnitId, unit3.name actualApproveUnitName, log.reviewStatus_id logReviewstatusId, log.reviewcontent logReviewcontent, item3.id approveUnitGradeId, item3.itemvalue approveUnitGradeName, item2.id logStatusId,item2.itemvalue logStatusName
from t_equip_base p inner join t_approve_log log on log.business_id = p.id inner join t_approve_process pro on pro.id = log.process_id inner join t_approve_step step on step.id = log.step_id left join t_sys_unit unit1 on unit1.id = log.stepUnit_id left join t_sys_unit unit2 on unit2.id = p.unit_id left join user u1 on u1.id = log.stepUser_id left join user u2 on u2.id = p.applicant left join user u3 on u3.id = log.approvetor_id left join t_sys_unit unit3 on unit3.id = u3.unit_id left join sysarea area on area.id = p.area_id left join item item1 on item1.id = p.status_id left join item item2 on item2.id = log.status_id left join item item3 on item3.id = unit1.gradetype
where log.datasign = '1' and p.id = #{q, jdbcType=VARCHAR} order by log.logNumber desc LIMIT #{from}, #{size}

