mapper.select:
SELECT count(*) count from t_approve_log log inner join t_science_base p on log.business_id = p.id inner join t_approve_process pro on log.process_id = pro.id inner join t_approve_step step on log.step_id = step.id left join t_sys_unit unit1 on unit1.id = log.stepUnit_id left join t_sys_unit unit2 on unit2.id = p.unit_id left join user u1 on u1.id = log.stepUser_id left join user u2 on u2.id = p.applicant left join user u3 on u3.id = log.approvetor_id left join sysarea area on area.id = p.area_id left join item item1 on item1.id = p.status_id left join item item2 on item2.id = log.status_id left join item item3 on item3.id = log.reviewstatus_id left join item item4 on item4.id = p.reviewStatus_id
log.datasign = '1' and log.status_id = 'f381bea9-71be-4d3f-997d-54529e7b1b4a' and (unit1.id = #{user.unitId} or u1.id = #{user.id} or u3.id = #{user.id} ) and 1=1
${key} #{item}

SELECT p.id as projectId, p.applycode projectNumber, p.applyname projectName,p.type_id projectTypeId, p.reviewCotent projectReviewContent,p.log_id projectLastLogId, p.applytime projectDeclareTime, pro.id processId, pro.number processNumber, pro.name processName, step.id stepId, step.stepnumber stepNumber, step.parentstepnumber stepParentNumber, step.stepname stepName, log.id logId, log.createtime logCreatetime, log.lastupdatetime logLastupdatetime, log.stepNumber logStepNumber, log.logNumber longNumber, log.starttime logStarttime, log.endtime logEndtime, log.reviewscore_id logReviewcore, log.reviewcontent logReviewcontent, area.id areaId, area.name areaName, u1.id approverId, u1.name approverName, u2.id declarerId, u2.name declarerName, u3.id actualApproverId, u3.name actualApproverName, unit1.id approveUnitId, unit1.name approveUnitName, unit2.id declareUnitId, unit2.name declareUnitName, item1.id projectStatusId, item1.itemvalue projectStatusName, item2.id logStatusId, item2.itemvalue logStatusName, item3.id logReviewstatusId, item3.itemvalue logReviewstatus, item4.id projectReviewStatus, item4.itemvalue projectReviewStatusName from t_approve_log log inner join t_science_base p on log.business_id = p.id inner join t_approve_process pro on log.process_id = pro.id inner join t_approve_step step on log.step_id = step.id left join t_sys_unit unit1 on unit1.id = log.stepUnit_id left join t_sys_unit unit2 on unit2.id = p.unit_id left join user u1 on u1.id = log.stepUser_id left join user u2 on u2.id = p.applicant left join user u3 on u3.id = log.approvetor_id left join sysarea area on area.id = p.area_id left join item item1 on item1.id = p.status_id left join item item2 on item2.id = log.status_id left join item item3 on item3.id = log.reviewstatus_id left join item item4 on item4.id = p.reviewStatus_id
log.datasign = '1' and log.status_id = 'f381bea9-71be-4d3f-997d-54529e7b1b4a' and (unit1.id = #{user.unitId} or u1.id = #{user.id} or u3.id = #{user.id} ) and 1=1
${key} #{item}
ORDER BY ${sort} ${order} LIMIT #{from}, #{size}
