mapper.select:
select b.id, b.applytime, b.applicant, b.applyname, b.unit_id, b.applycode, b.status_id, b.reviewstatus_id, b.reviewcontent, b.process_id, b.log_id, b.classify_id, b.creator_id, b.area_id, b.createtime, b.lastUpdateTime, b.updator_id, b.starttime, b.endtime, b.projectcontacts, b.phone, b.worksummary,
item1.itemvalue as statusName, u.name as applicantName, unit.name as unitName, item2.itemvalue as reviewstatusName, item3.id gradeTypeId, item3.itemvalue gradeTypeName
FROM t_hiddanger_declare b left join item item1 on item1.id = b.status_id left join user u on u.id = b.applicant left join t_sys_unit unit on unit.id = b.unit_id left join item item2 on item2.id = b.reviewstatus_id left join item item3 on item3.id = unit.gradetype where b.id = #{id,jdbcType=VARCHAR}

SELECT hd.id,hd.applyname,hd.applycode,hd.projectcontacts,hd.phone, unit.name as unitName, hd.applytime, hd.process_id, hd.log_id,hd.status_id, unit.id unitId, unit.name unitName, u.name as applicantName,hd.applytime,hd.worksummary,im1.itemvalue statusName,im2.itemvalue reviewstatusName FROM t_hiddanger_declare hd left join user u on u.id = hd.unit_id left join item im1 on im1.id=hd.status_id left join item im2 on im2.id=hd.reviewstatus_id left join t_sys_unit unit on unit.id = hd.unit_id
${key} #{item}
ORDER BY ${sort} ${order} LIMIT #{from}, #{size}
